var data_jiangshi = [{pid:1,data:[{id:14,h1:"埃及伽刚特尔",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕"},{k:"移动速度",v:"4s/格/AN"}]},{id:17,h1:"普通僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"无"},{k:"移动速度",v:" 5s/格/AN"}]},{id:18,h1:"路障僵尸",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:19,h1:"铁桶僵尸",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:20,h1:"旗子僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"举着旗子的僵尸，通常意味着一大波僵尸的来临。"},{k:"移动速度",v:"5s/格/AN"}]},{id:21,h1:"木乃伊僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"无"},{k:"移动速度",v:"5s/格/AN"}]},{id:22,h1:"路障木乃伊",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:23,h1:"铁桶木乃伊",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:24,h1:"旗子木乃伊",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"举着旗子的僵尸，通常意味着一大波僵尸的来临。"},{k:"移动速度",v:"5s/格/AN"}]},{id:25,h1:"太阳神僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：试图偷走阳光被<br>2：一个太阳神僵尸最多偷取250阳光<br>3: 它被杀死或魅惑后会返还阳光<br>4：偷取阳光时会停止行走"},{k:"移动速度",v:"5s/格/AN"}]},{id:26,h1:"骆驼僵尸",sum:"<p class=\"title\">僵尸体力</p>（每个僵尸）：250（骆驼板子）,240（本体）",content:[{k:"详细解析",v:"1：此类僵尸成群出现，数量至少为三个<br>2：在挡板未碎之前，这些僵尸动作一致，一只因某种原因停下来时所有的都会停下来<br>3：在挡板未碎之前，这些僵尸可以共享冰系状态，但火系攻击只能解冻被打中的僵尸"},{k:"移动速度",v:"10s/格/AN，5s/格/AN（挡板碎后）"}]},{id:27,h1:"探险者僵尸",sum:"<p class=\"title\">僵尸体力</p>350",content:[{k:"详细解析",v:"1：火把可以烧毁除极低矮植物以外的所有植物<br>2：冰系攻击可以灭掉火炬，火系攻击可以让火把复燃（中文安卓版第二点不成立）<br>3：此类僵尸魅惑后只会啃食，不会烧僵尸<br>4：此类僵尸不会烧毁全息坚果制造的屏障，只会进行啃食"},{k:"移动速度",v:"3.5s/格/AN"}]},{id:28,h1:"起墓僵尸",sum:"<p class=\"title\">僵尸体力</p>540",content:[{k:"详细解析",v:"1：向没有植物的位置丢出骨头，变成石碑（每过5秒扔两个）<br>2：不向第九列和第一列扔骨头<br>3：扔出的骨头可以落在破损石碑上来修复它<br>4：啃食时不会制造石碑<br>5：魅惑死神不会吐出骨头"},{k:"移动速度",v:"5s/格/AN"}]},{id:29,h1:"高级木乃伊僵尸",sum:"<p class=\"title\">僵尸体力</p>1200（棺材），405（本体）",content:[{k:"详细解析",v:"1：棺材破碎时可以抵消本次攻击的剩余伤害<br>2：棺材破碎后会愣一下，然后改变运动速度<br>3：棺材破碎前黄油和巴豆对他无效"},{k:"移动速度",v:"5s/格/AN，后2s/格/AN（失去棺材）"}]},{id:91,h1:"小猫木乃伊",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"会被巨人僵尸投掷入你的防御体系"},{k:"移动速度",v:"4s/格/AN"}]},{id:92,h1:"狮身人面像",sum:"<p class=\"title\">僵尸体力</p>18500",content:[{k:"详细解析",v:"1：召唤除旗帜木乃伊僵尸、太阳神僵尸、骆驼僵尸队外的埃及僵尸<br>2：发射导弹炸毁植物，在瞄准点周围3x3面积的空地上随机制造两个墓碑<br>3：跳跃，落点为8-9列上任意面积为2x3的区域，落地后在落点范围内触发一次攻击判定<br>4：冲刺，在本体所在的两行上快速前进并在冲刺过程中始终保持攻击判定，可被任意植物必杀效果所阻挡"},{k:"移动速度",v:"0.1s/格/AN（冲刺）"}]}]},{pid:2,data:[{id:15,h1:"海盗伽刚特尔",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕"},{k:"移动速度",v:"4s/格/AN"}]},{id:30,h1:"海盗僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"无"},{k:"移动速度",v:"5s/格/AN"}]},{id:31,h1:"路障海盗",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:32,h1:"木桶海盗",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:33,h1:"旗子海盗",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"举着旗子的僵尸，通常意味着一大波僵尸的来临。"},{k:"移动速度",v:"5s/格/AN"}]},{id:34,h1:"流氓僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：飞索僵尸利用他的绳索快速荡到5列<br>2：落点有一定几率是第六列，若在水路则导致落入水中<br>3：未落地的飞索僵尸判定为浮空飞行状态<br>4：只有出场僵尸有飞索时才会出现特殊事件RaidingParty（空袭）"},{k:"移动速度",v:"5s/格/AN"}]},{id:35,h1:"海鸥僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：始终处于浮空飞行状态<br>2：会被黄油所秒杀<br>3：不会进入冰系冰冻状态<br>4：飞行时无视低矮植物<br>5：吃了巴豆后头会放屁"},{k:"移动速度",v:"5s/格/AN"}]},{id:36,h1:"滚桶僵尸",sum:"<p class=\"title\">僵尸体力</p>1100（桶），270（本体）",content:[{k:"详细解析",v:"1：桶爆了会蹦出2个小鬼僵尸<br>2：地刺可以爆掉桶<br>3：滚筒和僵尸状态共享"},{k:"移动速度",v:"5s/格/AN"}]},{id:37,h1:"小鬼僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：会被巨人僵尸投掷入你的防御体系<br>2：会因为滚筒的破裂或小鬼炮的发射而进入战场"},{k:"移动速度",v:"4s/格/AN"}]},{id:38,h1:"矮人大炮",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：发射海盗小鬼僵尸到第五列<br>2：小鬼落点有一定几率是第六列，若为水路则会落入水中<br>3：发射完5只海盗小鬼僵尸后会解体，解体时向任意一行的3-6列发射约10只海盗小鬼僵尸<br>4：带能量豆的小鬼炮发射的小鬼不带能量豆<br>5：发射的小鬼未落地前始终处于浮空飞行状态<br>6：全息坚果的屏障可以阻止小鬼的登陆"},{k:"移动速度",v:"无"}]},{id:39,h1:"船长僵尸",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：可让鹦鹉僵尸偷走植物<br>2：鹦鹉被消灭后不会回来<br>3：鹦鹉回去后再飞出来不会回复体力<br>4：魅惑船长不会释放鹦鹉"},{k:"移动速度",v:"5s/格/AN"}]},{id:40,h1:"僵尸鹦鹉",sum:"<p class=\"title\">僵尸体力</p>300",content:[{k:"详细解析",v:"1：偷走植物后回到船长头上，准备下次偷取<br>2：鹦鹉只会偷取每路最靠右的非极低矮植物<br>3：鹦鹉可被黄油秒杀<br>4：鹦鹉一旦飞出就始终处于浮空飞行状态"},{k:"移动速度",v:"0.5s/格/AN"}]},{id:93,h1:"甲板步行者",sum:"<p class=\"title\">僵尸体力</p>26500",content:[{k:"详细解析",v:"1：召唤除旗帜海盗僵尸、飞索僵尸、小鬼炮、僵尸鹦鹉和海鸥僵尸外的海盗僵尸<br>2：向任意一行的3-6列发射约10只海盗小鬼僵尸<br>3：跳跃，落点为8-9列上任意面积为2x3的区域，落地后在落点范围内触发一次攻击判定<br>4：冲刺，在本体所在的两行上快速前进并在冲刺过程中始终保持攻击判定，可被除地刺外任意植物必杀效果所阻挡<br>5：免疫黄油定身"},{k:"移动速度",v:"0.1s/格/AN（冲刺）"}]}]},{pid:3,data:[{id:16,h1:"西部伽刚特尔",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕"},{k:"移动速度",v:"4s/格/AN"}]},{id:41,h1:"牛仔僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"帽子受到40伤害时掉落，受到的伤害全部转加给本体"},{k:"移动速度",v:"5s/格/AN"}]},{id:42,h1:"路障牛仔",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:43,h1:"铁桶牛仔",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:44,h1:"旗子牛仔",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：帽子受到40伤害时掉落，受到的伤害全部转加给本体<br>2：它的来袭往往标志着一大波僵尸的来袭"},{k:"移动速度",v:"5s/格/AN"}]},{id:45,h1:"勘探者僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：炸药引爆后僵尸会被炸飞至第一列，而后向右移动<br>2：任意冰系攻击会熄灭他的炸药，但火系攻击无法点燃<br>3：在被炸到空中时属于浮空飞行状态<br>4：魅惑淘金被炸飞后依然向右走"},{k:"移动速度",v:"5s/格/AN"}]},{id:46,h1:"钢琴僵尸",sum:"<p class=\"title\">僵尸体力</p>1200",content:[{k:"详细解析",v:"1：钢琴家僵尸的钢琴声可使所有的普通僵尸、路障僵尸、铁桶僵尸跳舞，并可使正在跳舞的僵尸随意换行，他的钢琴可以碾压植物，刚进场时可碾压刚放置的灰烬类植物<br>2：地刺可以扎爆钢琴"},{k:"移动速度",v:"4s/格/AN（未完全进场），7s/格/AN（完全进场）"}]},{id:47,h1:"斗篷僵尸",sum:"<p class=\"title\">僵尸体力</p>60（雨披），1100（金属板），270（本体）",content:[{k:"详细解析",v:"1：有时雨披后藏有金属板<br>2：安卓中文版的铁板可以抵挡两次灰烬武器（第一次掉斗篷，第二次掉铁板）<br>3：在非安卓中文版中在斗篷未掉之前若一次性受到大于240伤害的攻击，不管有无铁板都会死亡<br>4：斗篷未掉时铁板依然会被磁力菇吸走"},{k:"移动速度",v:"5s/格/AN"}]},{id:48,h1:"养鸡僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：携带10~14只僵尸鸡<br>2：当受到的伤害达到60时才会放出僵尸鸡<br>3：当该僵尸在黄油定身或冰系冻结状态下被杀死、被黄瓜炸死、被挑（吹）飞时不会释放僵尸鸡<br>4：魅惑鸡贼也可以释放僵尸鸡"},{k:"移动速度",v:"5s/格/AN"}]},{id:49,h1:"僵尸鸡",sum:"<p class=\"title\">僵尸体力</p>1",content:[{k:"详细解析",v:"1：僵尸鸡能极为迅速地突破你的防御体系<br>2：该僵尸触碰魅惑菇后会被魅惑但魅惑菇不会消失，魅惑鸡一碰到僵尸就死<br>3：该僵尸触碰吃能量豆的魅惑菇后会被魅惑但魅惑菇不会消失，魅惑鸡可以吃掉两只僵尸鸡"},{k:"移动速度",v:"0.5s/格/AN"}]},{id:50,h1:"僵尸牛",sum:"<p class=\"title\">僵尸体力</p>1300",content:[{k:"详细解析",v:"1：僵尸牛前方遇到障碍物（植物/墓碑）时会停下它奔跑的脚步，并把骑牛小鬼投掷到你的防御体系中<br>2：小鬼落点是从牛的位置向左数3格，可直接触发小车<br>3：小鬼在未飞出时如果牛死了，小鬼也无法战斗<br>4：牛会受到电脉桃的干扰"},{k:"移动速度",v:"5s/格/AN（刚进场），1.5s/格/AN（狂奔），5s/格/AN（正常）"}]},{id:51,h1:"骑牛僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：会因为僵尸牛的投掷而进入战场<br>2：会被巨人僵尸投掷入你的防御体系"},{k:"移动速度",v:"4s/格/AN"}]},{id:52,h1:"雪人僵尸",sum:"<p class=\"title\">僵尸体力</p>1000（正常模式），1350（暴走雪人）",content:[{k:"详细解析",v:"1：进场一段时间后会迅速逃跑<br>2：僵尸雪人随身携带的午餐盒内会有：中文版一定几率会出现金币，有一定几率会出现植物碎片，一定几率会出现钥匙；国际版会出现1000金币<br>3：会受到电脉桃的影响<br>4：逃跑时不会啃食植物"},{k:"移动速度",v:"5s/格/AN，逃跑时2.1s/格/AN"}]},{id:94,h1:"黄牛战车",sum:"<p class=\"title\">僵尸体力</p>25500",content:[{k:"详细解析",v:"1：召唤除旗帜牛仔僵尸、僵尸鸡、僵尸牛外的牛仔僵尸<br>2：发射四个导弹炸毁矿车上的植物<br>3：发射三个导弹炸毁非轨道上的植物植物<br>4：跳跃，落点为8-9列上任意面积为2x3的区域，落地后在落点范围内触发一次攻击判定<br>5：冲刺，在本体所在的两行上快速前进并在冲刺过程中始终保持攻击判定，可被任意植物必杀效果所阻挡"},{k:"移动速度",v:"0.1s/格/AN（冲刺）"}]}]},{pid:4,data:[{id:1,h1:"锤子僵尸",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：遭到直线攻击时会快速旋转身体，发出时长为2.5秒的旋风，可反弹子弹；子弹会对自身正前方的第一株植物造成伤害（低矮植物除外）。<br>2：制造锤子旋风时停止前行，旋风结束后需要在原地喘息片刻。<br>3：只能反弹直射类植物的攻击"},{k:"移动速度",v:"6s/格/AN"}]},{id:2,h1:"自爆僵尸",sum:"<p class=\"title\">僵尸体力</p>480",content:[{k:"详细解析",v:"1：碰到植物以该植物为中心3x3秒杀<br>2：炸弹受到严格大于160伤害时掉落，掉落时炸弹受到的伤害全部转加给本体<br>3：冰系攻击可以直接使炸弹掉落"},{k:"移动速度",v:"4s/格/AN"}]},{id:3,h1:"气功僵尸",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：发功将前方一排植物向右吸一格，每走一格发功一次<br>2：在刚进场的15s之内不会发功"},{k:"移动速度",v:"7s/格/AN"}]},{id:4,h1:"喝酒僵尸",sum:"<p class=\"title\">僵尸体力</p>870",content:[{k:"详细解析",v:"1：喝酒后自身的攻击力、速度、除临界状态的剩余血量翻一倍<br>2：只有当喝酒完成即开始暴走时自身能力才会翻倍"},{k:"移动速度",v:"4.5s/格/AN（喝酒前），2.1s/格/AN（喝酒后）"}]},{id:5,h1:"铜锣僵尸",sum:"<p class=\"title\">僵尸体力</p>300",content:[{k:"详细解析",v:"1：每次召唤2-3个自爆僵尸<br>2：走3格进行一次召唤"},{k:"移动速度",v:"4s/格/AN"}]},{id:6,h1:"武僧路障僵尸",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:7,h1:"武僧僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"可从兵器架上得到武器"},{k:"移动速度",v:"5s /格/AN"}]},{id:8,h1:"武僧铁茶壶僵尸",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁茶壶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:9,h1:"铁茶壶僵尸",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁茶壶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:10,h1:"路障僵尸",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:11,h1:"火把僵尸",sum:"<p class=\"title\">僵尸体力</p>350",content:[{k:"详细解析",v:"1：火把可以烧毁除极低矮植物以外的所有植物<br>2：冰系攻击可以灭掉火炬，火系攻击可以让火把复燃（中文安卓版第二点不成立）"},{k:"移动速度",v:"3.5s/格/AN"}]},{id:12,h1:"旗子僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：当僵尸从兵器架上取下旗帜时可召唤出3个普僵<br>2：它的来袭往往标志着一大波僵尸的来袭"},{k:"移动速度",v:"5s/格/AN"}]},{id:13,h1:"功夫僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"可从兵器架上得到武器"},{k:"移动速度",v:"5s /格/AN"}]},{id:78,h1:"武僧旗子",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"它的来袭往往标志着一大波僵尸的来袭"},{k:"移动速度",v:"5s/格/AN"}]},{id:79,h1:"火药魔头",sum:"<p class=\"title\">僵尸体力</p>48000",content:[{k:"详细解析",v:"1：召唤除旗帜外的功夫僵尸<br>2：扔出炸弹炸毁3x3面积植物, 可通过铲掉植物来防御<br>3：用炸药把自己炸飞，落点为7-9列上任意面积为2x3或2x2的区域，落地后在落点范围内触发一次攻击判定<br>4：发射出2-3个小鬼至5列<br>5：发射一排火箭小鬼，火箭可用手指挑飞"},{k:"移动速度",v:"无"}]},{id:80,h1:"喝酒武僧",sum:"<p class=\"title\">僵尸体力</p>870",content:[{k:"详细解析",v:"1：喝酒后自身的攻击力、速度、除临界状态的血量翻一倍<br>2：可在距植物还有一格时对植物发动攻击<br>3：只有当喝酒完成即开始暴走时自身能力才会翻倍"},{k:"移动速度",v:"9s/格/AN（喝酒前），3.5s/格/AN（喝酒后）"}]},{id:81,h1:"双节棍武僧",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：遭到直线攻击时会快速旋转双节棍4秒，反弹子弹；子弹会对自身正前方的第一株植物造成伤害（低矮植物除外）。<br>2：转动双节棍时停止前行，结束后需要在原地喘息片刻。<br>3：可用双节棍打飞挡路的植物"},{k:"移动速度",v:"5s/格/AN"}]},{id:82,h1:"大刀武僧",sum:"<p class=\"title\">僵尸体力</p>450",content:[{k:"详细解析",v:"用刀重击植物"},{k:"移动速度",v:"4.5s/格/AN"}]},{id:83,h1:"轻功小鬼",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：助跑后发动轻功飞过3格，可被高坚果阻挡<br>2：使用轻功时处于浮空飞行状态<br>3：入场后需要行走一段距离后才能助跑"},{k:"移动速度",v:"5s/格/AN"}]},{id:84,h1:"武僧火把僵尸",sum:"<p class=\"title\">僵尸体力</p>350",content:[{k:"详细解析",v:"1：火把可以烧毁除极低矮植物以外的所有植物<br>2：冰系攻击可以灭掉火炬，火系攻击可以让火把复燃（中文安卓版第二点不成立）"},{k:"移动速度",v:"3.5s/格/AN"}]},{id:85,h1:"大汉铜人",sum:"<p class=\"title\">僵尸体力</p>7000",content:[{k:"详细解析",v:"1：受伤后会暴走，速度加快<br>2：出场前会在壳里，等到了时间才会出场<br>3：在壳中时免疫黄瓜的攻击<br>4：对植物造成秒杀伤害（钢地刺需要3下），对僵尸造成1500伤害"},{k:"移动速度",v:"6s/格/AN（正常行走），4s/格/AN（暴走）"}]},{id:86,h1:"侠客铜人",sum:"<p class=\"title\">僵尸体力</p>7000",content:[{k:"详细解析",v:"1：利用锁链移动到其他行路<br>2：出场前会在壳里，等到了时间才会出场<br>3：在壳中时免疫黄瓜的攻击<br>4：从空中降落时秒杀该格的植物<br>5：对植物造成秒杀伤害（钢地刺需要3下），对僵尸造成1500伤害"},{k:"移动速度",v:"6s/格/AN"}]},{id:87,h1:"气功铜人",sum:"<p class=\"title\">僵尸体力</p>7000",content:[{k:"详细解析",v:"1：发功将前方一排植物向右吸一格，每走一格发功一次<br>2：出场前会在壳里，等到了时间才会出场<br>3：在壳中时免疫黄瓜的攻击<br>4：对植物造成秒杀伤害（钢地刺需要3下），对僵尸造成1500伤害"},{k:"移动速度",v:"8s/格/AN"}]},{id:88,h1:"持刀僵王",sum:"<p class=\"title\">僵尸体力</p>80000",content:[{k:"详细解析",v:"1：召唤除旗帜外的武僧僵尸和铜人<br>2：用刀波使该行植物退后两格<br>3：移动，落点为7-9列上任意面积为2x3或2x2的区域，落地后在落点范围内触发一次攻击判定<br>4：使用飞龙在天消灭一排植物<br>5：发射一排飞天小鬼"},{k:"移动速度",v:"无"}]}]},{pid:5,data:[{id:53,h1:"未来僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"普通僵尸"},{k:"移动速度",v:"5s/格/AN"}]},{id:54,h1:"未来路障僵尸",sum:"<p class=\"title\">僵尸体力</p>370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:55,h1:"未来铁桶僵尸",sum:"<p class=\"title\">僵尸体力</p>1100（头锥），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:56,h1:"未来旗帜僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"它的来袭往往标志着一大波僵尸的来袭"},{k:"移动速度",v:"5s/格/AN"}]},{id:57,h1:"火箭僵尸",sum:"<p class=\"title\">僵尸体力</p>450",content:[{k:"详细解析",v:"1：火箭僵尸可以借助他的火箭越过的植物<br>2：无法越过高坚果和全息坚果的屏障<br>3：始终处于浮空飞行状态"},{k:"移动速度",v:"3s/格/AN"}]},{id:58,h1:"屏障僵尸",sum:"<p class=\"title\">僵尸体力</p>1200（屏障），460（本体）",content:[{k:"详细解析",v:"1：屏障僵尸召唤的电子护盾可以抵挡所在行及相邻两行来自正前方的单体伤害<br>2：屏障被摧毁后在5s之后该僵尸会制造另一个全新的屏障<br>3：范围攻击和溅射伤害可以2次伤害屏障<br>4：屏障消失时会吸收一些伤害，但不会发生伤害转移<br>5：电脉桃可以使其进入短路紊乱状态，同时屏障也会消失<br>6：啃食时不会召唤新的屏障<br>7：屏障不会回血<br>8：屏障类似于浮空飞行状态的僵尸，无视地刺"},{k:"移动速度",v:"5s/格/AN"}]},{id:59,h1:"小虫小鬼机器人",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：会通过BotSwarm（机器人群袭）空降进入战场<br>2：会被巨人僵尸投掷入你的防御体系<br>3：只有出场僵尸有小虫小鬼时才会出现特殊事件Bot Swarm（机器人群袭）<br>4：电脉桃可以使其进入短路紊乱状态"},{k:"移动速度",v:"3s/格/AN"}]},{id:60,h1:"机械路障僵尸",sum:"<p class=\"title\">僵尸体力</p>2500",content:[{k:"详细解析",v:"1：电脉桃可以使其进入短路紊乱状态<br>2：血量很高，帮助僵尸抵挡火力"},{k:"移动速度",v:"7s/格/AN"}]},{id:61,h1:"迪斯科3000",sum:"<p class=\"title\">僵尸体力</p>2000",content:[{k:"详细解析",v:"1：每隔一段时间会在上下左右四个相邻格子中召唤迪斯科火箭僵尸<br>2：电脉桃可以使其进入短路紊乱状态<br>3：若在召唤途中进入短路紊乱状态，召唤的迪斯科火箭僵尸全部死亡<br>4：召唤的僵尸不和该僵尸行动同步<br>5：如果进入下一个召唤周期，不管上一次召唤的僵尸状态如何都会继续召唤<br>6：召唤僵尸前后的一段时间停止行走<br>7：啃食时不召唤僵尸"},{k:"移动速度",v:"5s/格/AN"}]},{id:62,h1:"迪斯科火箭",sum:"<p class=\"title\">僵尸体力</p>450",content:[{k:"详细解析",v:"1：在特龙-迪斯科3000的召唤下4个结伙出现<br>2：可以借助他的火箭越过的植物<br>3：无法越过高坚果和全息坚果的屏障<br>4：始终处于浮空飞行状态"},{k:"移动速度",v:"3s/格/AN"}]},{id:63,h1:"机械橄榄球僵尸",sum:"<p class=\"title\">僵尸体力</p>2000",content:[{k:"详细解析",v:"1：将触碰到的植物向左推进一格，若顶满则顶飞最左侧的植物<br>2：对魅惑僵尸一次造成400伤害<br>3：可以直接摧毁全息坚果的屏障<br>4：不会推走极低矮的植物<br>5：电脉桃可以使其进入短路紊乱状态"},{k:"移动速度",v:"5s/格/AN"}]},{id:64,h1:"机械巨人僵尸",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕<br>4：向任意一行的1-4列发射敌我不分的伤害在60-660之间（平均在300左右）的激光攻击<br>5：一个攻击周期可以砸两次"},{k:"移动速度",v:"4s/格/AN"}]},{id:65,h1:"僵尸机器人",sum:"<p class=\"title\">僵尸体力</p>54000",content:[{k:"详细解析",v:"1：召唤除旗帜，机械路障，迪斯科3000，迪斯科喷气机外的未来僵尸<br>2：发射导弹炸毁植物(顺便炸毁植物下方的能量格)<br>3：跳跃或移动，落点为7-9列上任意面积为2x3或2x2的区域，落地后在落点范围内触发一次攻击判定<br>4：冲刺，在本体所在的两行上快速前进并在冲刺过程中始终保持攻击判定，可被该行任意植物必杀效果或三叶草所阻挡"},{k:"移动速度",v:"0.1s/格 /AN（冲刺）"}]},{id:89,h1:"巨人僵尸",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕"},{k:"移动速度",v:"4s/格/AN"}]},{id:90,h1:"小鬼僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"会被巨人僵尸投掷入你的防御体系"},{k:"移动速度",v:"4s/格/AN"}]}]},{pid:6,data:[{id:66,h1:"农夫僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"普通僵尸"},{k:"移动速度",v:"5s/格/AN"}]},{id:67,h1:"路障农夫",sum:"<p class=\"title\">僵尸体力</p>体力：370（头锥），270（本体）",content:[{k:"详细解析",v:"路障会给予它较高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:68,h1:"铁桶农夫",sum:"<p class=\"title\">僵尸体力</p>1100（铁桶），270（本体）",content:[{k:"详细解析",v:"铁桶会给予它很高的防御力"},{k:"移动速度",v:"5s/格/AN"}]},{id:69,h1:"旗帜僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"它的来袭往往标志着一大波僵尸的来袭"},{k:"移动速度",v:"5s/格/AN"}]},{id:70,h1:"小鬼僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：会因为Necromancy（招魂术）召唤或自己行走来进入战场<br>2：会被巨人僵尸投掷入你的防御体系"},{k:"移动速度",v:"4s/格/AN"}]},{id:71,h1:"小丑僵尸",sum:"<p class=\"title\">僵尸体力</p>600",content:[{k:"详细解析",v:"1：免疫一切实体性攻击（除近距离攻击），受到此类攻击时会开始旋转，一段时间后将攻击返还，伤害植物<br>2：除豌豆射手、寒冰射手、三重射手、双发射手、玉米投手之外的所有植物必杀都可以伤害到它<br>3：它无法反弹溅射伤害<br>4：当遭到植物实体性攻击时开始旋转，不受到实体性攻击时停止旋转"},{k:"移动速度",v:"5s/格 /AN（正常状态）， 3s/格 /AN（旋转）"}]},{id:72,h1:"骑士僵尸",sum:"<p class=\"title\">僵尸体力</p>1600（骑士帽），270（本体）",content:[{k:"详细解析",v:"头盔会给予它极高的防御力"},{k:"移动速度",v:"5s/格 /AN"}]},{id:73,h1:"农夫巨人",sum:"<p class=\"title\">僵尸体力</p>3600",content:[{k:"详细解析",v:"1：身躯庞大，通过砸扁植物来突入防线<br>2：体力少于一半扔小鬼到第三列，若此时已走过第三格则不投掷（魅惑后除外）<br>3：不可被浮空指改变行路或挑出屏幕"},{k:"移动速度",v:"4s/格/AN"}]},{id:74,h1:"巫师僵尸",sum:"<p class=\"title\">僵尸体力</p>450",content:[{k:"详细解析",v:"1：能够将植物转变成绵羊，变成绵羊后植物无法攻击，但也不会受到攻击，僵尸会直接穿过变成羊的植物。<br>2：优先将所在行的植物变成僵尸，变完本行植物后向最近行的植物扩散，施法间隔大约10秒一个。<br>3：变成羊的植物只有将对应施法的巫师僵尸杀掉才能变回植物，否则将一直是羊。"},{k:"移动速度",v:"5s/格 /AN"}]},{id:75,h1:"僵尸国王",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"1：登场于右起第一列，不会移动，不会直接对植物造成伤害 <br>2：会让自己周围的铁通，路障，普僵变成骑士僵尸，防具能承受的伤害会重新计算。"},{k:"移动速度",v:"无"}]},{id:76,h1:"小鬼龙僵尸",sum:"<p class=\"title\">僵尸体力</p>270",content:[{k:"详细解析",v:"免疫火属性伤害，BOSS黑暗巨龙的火焰喷射不会对它造成伤害。"},{k:"移动速度",v:"5s/格 /AN"}]},{id:77,h1:"黑暗巨龙",sum:"<p class=\"title\">僵尸体力</p>54000",content:[{k:"详细解析",v:"1：召唤绝大多数的黑暗世界僵尸，僵尸从右边第四列出现，所以将植物种在右边第三列不会损坏。<br>2：向任意两行喷射火焰，直接将你这两行的植物全部消灭，包括僵尸（小鬼龙僵尸除外），4秒内无法在这两行种植物。<br>3：投掷出火焰球能烧毁命中格的植物，此格在烧毁后4秒内无法种植植物，有的火焰球可以孵化出小鬼龙僵尸。 <br>4：在黑暗巨龙僵尸出场时会将最右边的两列的格子烧毁，此格子无法种植植物。"},{k:"移动速度",v:"无"}]}]}];